// メインタイトル用_上からからフワッと出現
$(function() {
  //画面をスクロールするとイベントが発動する
  $(window).on('load', function() {
    
    //フェードインさせたい要素の位置をずらす
    $('.fadein-main-top').css({
      'top': '40%',
      'left': '50%',
      'transform': 'translate(-50%, -50%)',
      'opacity': '0',
    });
    
    //スクロールバーの位置を取得
    var scroll = $(window).scrollTop();

    //ウィンドウの高さを取得
    var windowHeight = $(window).height();

    $('.fadein-main-top').each(function() {
      //フェードインさせたい要素の縦位置を取得
      var elemPos = $(this).offset().top;

      //要素がウィンドウの中に入ってからさらに400pxスクロールしたら要素をフェードインする
      if (scroll > elemPos - windowHeight + 0) {
        $(this).css({
          'top': '30%',
          'left': '50%',
          'transform': 'translate(-50%, -50%)',
          'opacity': '1',
        });
      }
    });
  });
});

// 右からフワッと出現
$(function() {
    //画面をスクロールするとイベントが発動する
    $(window).scroll(function() {
      
      //フェードインさせたい要素の位置をずらす
      $('.fadein-right').css({
        'opacity': '0',
        'transform': 'translateX(50px)'
      });
      
      //スクロールバーの位置を取得
      var scroll = $(window).scrollTop();
  
      //ウィンドウの高さを取得
      var windowHeight = $(window).height();
  
      $('.fadein-right').each(function() {
        //フェードインさせたい要素の縦位置を取得
        var elemPos = $(this).offset().top;
  
        //要素がウィンドウの中に入ってからさらに400pxスクロールしたら要素をフェードインする
        if (scroll > elemPos - windowHeight + 200) {
          $(this).css({
            'opacity': '1',
            'transform': 'translateX(0)'
          });
        }
      });
    });
});

